mapper.select:
SELECT 

FROM  llm_wkstadoc b

WHERE a.id = #{id}

SELECT 

FROM llm_wkstadoc b



			AND frocktype = #{frockType}


		AND b.workstationcode like '%'||#{workstationcode}||'%'




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM llm_sta_frocktype a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
